import { PayPalButton } from "react-paypal-button-v2";
// import axios from "axios";

import NavBar from "../components/NavBar";
import Footer from "../components/Footer";

export default class CartScreen extends Component {
    state={

    }

    componentDidMount(){
        // const name = localStorage.getItem("name");
        // const image = localStorage.getItem("image");
        // const size = localStorage.getItem("size");
        // const quantity = localStorage.getItem("quantity");
        // const price = localStorage.getItem("price");
        // const subtotal = localStorage.getItem("subtotal");
        // this.setState({name, image, size, quantity, price, subtotal});

        let items = JSON.parse(sessionStorage.getItem("items"));
        this.setState({items});

    }

    render() {
        var item = JSON.parse(sessionStorage.getItem("items"));
        console.log(item.lenght());
        return (
            <div>
                <NavBar
                    username={this.props.username}
                    onLogin={this.props.onLogin}
                />
                <div className="row">
                    <div className="col-1"></div>
                    <div className="col-10 text-center">
                        <table class="table">
                            <thead>
                                <tr>
                                    <th scope="col">MY CART</th>
                                    <th scope="col">Size </th>
                                    <th scope="col">Quantity</th>
                                    <th scope="col">Price </th>
                                    <th scope="col">Total</th>
                                </tr>
                            </thead>

                            {/* <tbody>
                                <tr>
                                    <th scope="col"><img className= "cart-img"
                                        src={this.state.items.image ? this.state.items.image : ""} /></th>
                                    <th scope="col">{this.state.items.size ? this.state.items.size : ""}</th>
                                    <th scope="col">{this.state.items.quantity ? this.state.items.quantity : ""}</th>
                                    <th scope="col">{this.state.items.price ? this.state.items.price : ""} </th>
                                    <th scope="col">{this.state.items.subtotal ? this.state.items.subtotal : ""}</th>

                                </tr>
                            </tbody>
                            <p>{this.state.items.name ? this.state.items.name : ""}</p> */}
                        </table>
                    </div>
                </div>
                <div className="text-center">
                    <PayPalButton
                        amount="100"
                        onSuccess={(details, data) => {
                            alert("Transaction completed by " + details.payer.name.given_name);

                            // OPTIONAL: Call your server to save the transaction
                            return fetch("/paypal-transaction-complete", {
                                method: "post",
                                body: JSON.stringify({
                                    orderID: data.orderID
                                })
                            });
                        }}
                    />
                </div>

                <Footer />
            </div>
        )
    }
}
